INSERT INTO `Zusammenfassung` VALUES ('00059',1,'1','a',NULL,'WEimarische Bibel/ mit Kupffern/ Nürnberg. 1652.','Biblia, Das ist/ Die gantze H. Schrifft, Altes und Newes Testaments Teutsch/ D. Martin Luthers : Auff gnädige Verordnung deß ... Fürsten und Herrn/ Herrn Ernsts/ Hertzogen zu Sachsen/ Jülich/ Cleve und Berg ... Von etlichen reinen Theologen, dem eigentlichen Wortverstand nach/ erkläret/ Wie hievon weiter in der Vorrede gehandelt wird. Darbey auch über die sonst gewönliche/ jetzt aber von newem corrigirte und wol verbesserte Biblische Register unter andern zu finden ein Bericht von Vergleichung der Jüdischen und Biblischen Monden/ Maß/ Gewicht und Müntz/ mit den Unserigen: Sowol auch eine Beschreibung der Stadt Jerusalem/ sambt unterschiedlichen Landtafeln und andern schönen Kupfferfiguren und derselben Beschreibung ... ; Ist auch zu End neben den Christlichen Haubt-Symbolis mit beygedruckt worden ein kurtzer und schöner Bericht von der Augspurgischen Confession/ sambt den Artickeln der Confession selbsten ... ','Biblia <dt.>','Nürnberg','Endter, Wolfgang d.Ä.','1652','2°','Libri Theologici','Theologie','Bibel','Druck','ger',NULL,'VD17','547:716901H','gebunden','WLB (Bibelsammlung) E799',NULL,NULL,'Luther, Martin',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
